var pages = flow.get("ns-config")

function genEntitiesPage(pageNum){
    var out_msgs = [ {payload:"pageType,cardEntities"}, { payload: "entityUpdHeading,"+pages[pageNum].heading } ]

    pages[pageNum].items.forEach(function (id, i) {

        var type = "delete"
        var iconId = 0
        var name = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.friendly_name")

        switch (id.substring(0,id.indexOf('.'))) {
            case "cover":
                type = "shutter"
                iconId = 0
                out_msgs.push({ payload: "entityUpd,"+(i+1)+","+iconId+","+name+","+type})
                break
            case "light":
                type = "light"
                iconId = 1
                var optVal = "0"
                if(global.get("homeassistant.homeAssistant.states['"+id+"'].state") == "on")
                    optVal = "1"
                out_msgs.push({ payload: "entityUpd,"+(i+1)+","+iconId+","+name+","+type+","+optVal})
                break
            case "sensor":
                type = "text"
                if(global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.device_class") == "temperature"){
                    iconId = 2
                }
                var optVal = global.get("homeassistant.homeAssistant.states['"+id+"'].state") + " " + global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.unit_of_measurement")
                out_msgs.push({ payload: "entityUpd,"+(i+1)+","+iconId+","+name+","+type+","+optVal})
                break
            case "button":
                type = "button"
                iconId = 3
                var optVal = "PRESS"
                out_msgs.push({ payload: "entityUpd,"+(i+1)+","+iconId+","+name+","+type+","+optVal})
                break
            case "delete":
                type = "delete"
                out_msgs.push({ payload: "entityUpd,"+(i+1)+",0,dc,"+type})
                break
            default:
                break
        }

    })



    return out_msgs

}

function genThermoPage(pageNum){
    id = pages[pageNum].item
    var out_msgs = [ {payload:"pageType,cardThermo"} ]

    let heading     = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.friendly_name")
    let currentTemp = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.current_temperature")*10
    let destTemp    = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.temperature")*10
    let status      = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.hvac_action")
    let minTemp     = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.min_temp")*10
    let maxTemp     = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.max_temp")*10
    //let stepTemp     = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.target_temp_step")*10
    let stepTemp     = 0.5*10

    out_msgs.push({ payload: "entityUpd,"+heading+","+currentTemp+","+destTemp+","+status+","+minTemp+","+maxTemp+","+stepTemp })


    return out_msgs
}


function handleButtonEvent(pageNum, words){
    var out_msgs = [ ]
    pages[pageNum].items.forEach(function (id, i) {
        var name = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.friendly_name")
        if(words[4]==name){
            if(words[6]=="OnOff"){
                var domain = "switch"
                if(id.startsWith("light"))
                    domain = "light"
                var action = "turn_off"
                if(words[7]=="1")
                    action = "turn_on"
                out_msgs.push( {payload: id, action: action, domain: domain})
            }

            if(words[6]=="up")
                out_msgs.push( {payload: id, action: "open_cover", domain: "cover"})
            if(words[6]=="stop")
                out_msgs.push( {payload: id, action: "stop_cover", domain: "cover"})
            if(words[6]=="down")
                out_msgs.push( {payload: id, action: "close_cover", domain: "cover"})

            if(words[6]=="brightnessSlider")
                out_msgs.push( {payload: id, action: "turn_on", domain: "lightBrightness", brightness: parseInt(words[7])})
            if(words[6]=="colorTempSlider")
                out_msgs.push( {payload: id, action: "turn_on", domain: "lightTemperature", temperature: parseInt(words[7])})

        }
    }

    )
    return out_msgs
}

function searchIdInPages(friendlyName){
    let id_ret = ""
    // search entity in pages
    pages.forEach(page => {
        if("items" in page){
            page.items.forEach(function (id, i) {
                var name = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes.friendly_name")
                if(name == friendlyName){
                    id_ret = id
                }
            })
        }
    })
    return id_ret
}

function scale(number, inMin, inMax, outMin, outMax) {
    return (number - inMin) * (outMax - outMin) / (inMax - inMin) + outMin;
}

function genDetailPage(type, friendlyName){
    var out_msgs = [ ]

    let id = searchIdInPages(friendlyName)
    if(type == "popupLight"){
        let switchVal = "0"
        if(global.get("homeassistant.homeAssistant.states['"+id+"'].state") == "on")
            switchVal = "1"

        let attr = global.get("homeassistant.homeAssistant.states['"+id+"'].attributes")

        let brightness = Math.trunc(scale(attr.brightness, 0, 255, 0, 100))

        let colortemp = Math.trunc(scale(attr.color_temp, attr.min_mireds, attr.max_mireds, 0, 100))

        out_msgs.push({ payload: "entityUpdateDetail,"+switchVal+","+brightness+","+colortemp})
    }



    return out_msgs

}


words = msg.payload.split(',')
if(words[0]=='event'){
    var pageNum = parseInt(words[2])
    pageNum = (pageNum % pages.length)
    pageNum = Math.abs(pageNum)
    flow.set("ns-currentPage", pageNum)

    if(words[1]=='pageOpen'){
        var retMsgs = []

        if(pages[pageNum].type == "cardEntities"){
            retMsgs = genEntitiesPage(pageNum)
        }else if(pages[pageNum].type == "cardThermo")
        {
            retMsgs = genThermoPage(pageNum)
        }

        return [retMsgs, null]
    }
    if(words[1]=='buttonPress'){
        return [null, handleButtonEvent(pageNum, words)]
    }
    if(words[1]=='pageOpenDetail'){
        return [genDetailPage(words[2], words[3]), null]
    }

}

